class Greeting{
	private String name;
	private int score;
	
	public Greeting(String name, int score) {
		this.name = name;
		this.score = score;
	}
	public String sayHello() {
		return "hello, " + name;
	}
	public String check_score() {
		String res = "";
		if(score > 60) {
			res = name+" 학생은 특정 점수 이상입니다";
		}else {
			res = name+" 학생은 특정 점수 이하";
		}
		return res;
	}
}

